<style>
    @keyframes breath {
        0% { opacity: 0; }
        50% { opacity: 1;}
        100% { opacity: 0; }
    }
</style>
<div class="honeycomb" style="height: 100%; display: grid; grid-template-columns: repeat(8, 1fr); grid-template-rows: repeat(16, 1fr); gap: 5px 0px;">
    <div style="animation: breath 10s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 5; grid-column-end: 7; grid-row-start: 1; grid-row-end: 5;"></div>
    <div style="animation: breath 14s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 2; grid-column-end: 4; grid-row-start: 4; grid-row-end: 8;"></div>
    <div style="animation: breath 8s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 4; grid-column-end: 6; grid-row-start: 4; grid-row-end: 8;"></div>
    <div style="animation: breath 9s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 6; grid-column-end: 8; grid-row-start: 4; grid-row-end: 8;"></div>
    <div style="animation: breath 12s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 1; grid-column-end: 3; grid-row-start: 7; grid-row-end: 11;"></div>
    <div style="animation: breath 6s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 3; grid-column-end: 5; grid-row-start: 7; grid-row-end: 11;"></div>
    <div style="animation: breath 13s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 7; grid-column-end: 9; grid-row-start: 7; grid-row-end: 11;"></div>
    <div style="animation: breath 15s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 4; grid-column-end: 6; grid-row-start: 10; grid-row-end: 14;"></div>
    <div style="animation: breath 11s infinite; background-image: url('../assets/images/ui/hexagon.svg'); background-size: contain; background-repeat: no-repeat; background-position: center center; transform: scaleX(1.13); grid-column-start: 2; grid-column-end: 4; grid-row-start: 13; grid-row-end: 17;"></div>
</div>
